import React from 'react'
import Item from "./Item"
import Header from "./Header"
import AddItem from "./AddItem"
import todosData from "./toDos"


class App extends React.Component{

    constructor(){
        super()
        this.state = {
            todos : todosData
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange = id => {
        const newTodos = this.state.todos.map(function(oldTodo){
            if(oldTodo.id === id) return {
                id: oldTodo.id,
                text: oldTodo.text,
                completed: !oldTodo.completed
            }
            else return oldTodo
        })

        const todoList = {
            todos : newTodos
        }

        this.setState( pre => {
            return todoList
        })

    }

    handleSubmit= info =>{
        let newTodos = Object.assign({}, this.state.todos)
        newTodos.push({
            id: this.state.todos.length + 1,
            text: info,
            completed : false
        })

        this.setState (pre => {
            return {todos:newTodos}
        })
    }




    render(){
        let todos = this.state.todos.map(element => {
            return <Item key = {element.id} id = {element.id} name = {element.text} checked = {element.completed} handleChange = {this.handleChange}/>
        });

        return (
            <div>
                <Header/>
                <AddItem handleAdd = {this.handleSubmit}/>
                {todos}
            </div>

        );

    }
}

export default App;
