import React from 'react'
import Item from "./Item"
import Header from "./Header"
import todosData from "./toDos"


class App extends React.Component{

    constructor(){
        super()
        this.state = {
            todos : todosData
        }

        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(id){
        this.setState(
            {todos : this.state.todos.map(
                function(todo){
                    let c = todo.completed
                    if(todo.id === id) c = !c
                    return{
                        id : todo.id,
                        text : todo.text,
                        completed : c
                    }
                })}
        )

    }




    render(){
        let todos = this.state.todos.map(function(element){
            return <Item key = {element.id} name = {element.text} checked = {element.completed} handleChange = {this.handleChange}/>
        });

        return (
            <div>
                <Header/>
                {todos}
            </div>

        );
    }
}

export default App;
