import React from 'react'
import Item from "./Item"
import Header from "./Header"
import todosData from "./toDos"


class App extends React.Component{

    constructor(){
        super()
        this.state = {
            todos : todosData
        }
        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(id){
        const newTodos = this.state.todos.map(function(oldTodo){
            if(oldTodo.id === id) oldTodo.completed = !oldTodo.completed
            return oldTodo
        })

        this.setState(function(){
            return newTodos
        })

    }




    render(){
        let todos = this.state.todos.map(function(element){
            return <Item key = {element.id} name = {element.text} checked = {element.completed} handleChange = {this.handleChange}/>
        });

        return (
            <div>
                <Header/>
                {todos}
            </div>

        );
    }
}

export default App;
